        public void Run()
        {

            string jsonFilePath = "d:\\AllTransactionsMenu.txt";

            string jsonData = File.ReadAllText(jsonFilePath);
            var menuData = JsonConvert.DeserializeObject<List<MenuCategory>>(jsonData);

            List<MenuItem> flatMenuItems = new List<MenuItem>();
            foreach (var category in menuData)
            {
                int sortOrder = 1;
                FlattenMenu(category.MenuList, null, ref sortOrder, flatMenuItems);
            }

            InsertIntoDatabase(flatMenuItems);

        }

        public void FlattenMenu(List<MenuItemData> menuList, int? parentId, ref int sortOrder, List<MenuItem> flatList)
        {
            foreach (var item in menuList)
            {
                var menuItem = new MenuItem
                {
                    Keyword = item.ItemKey,
                    Pid = parentId,
                    //  Text = item.ItemText,
                    MenuBaseItemId = 8888,    //  todo: patlayacak muhtemelen kullanılıyor mu bilmiyorum.
                    IsNew = item.IsNew,
                    SortOrder = sortOrder++,
                    CreatedDate = DateTime.UtcNow,
                    MenuGroupId = 1 // Varsayılan bir değer, isteğe bağlı değiştirilebilir
                };

                flatList.Add(menuItem);

                if (item.SubList != null && item.SubList.Count > 0)
                {
                    int childSortOrder = 1; // Alt seviye için tekrar 1'den başlaması sağlanıyor
                    FlattenMenu(item.SubList, menuItem.Id, ref childSortOrder, flatList);
                }
            }
        }

        public void InsertIntoDatabase(List<MenuItem> menuItems)
        {

            foreach (var item in menuItems)
            {

                _menuItemRepository.AddAsync(item);

            }
        }
